// Formatar √∫ltimas 5 intera√ß√µes para o user prompt
const interactions = $('getRecentInteractions').all();

if (interactions.length === 0) {
  return [{ json: { formatted_interactions: 'Nenhuma intera√ß√£o anterior registrada.' } }];
}

const formatLabels = [
  'Intera√ß√£o mais recente:',
  '2¬™ intera√ß√£o mais recente:',
  '3¬™ intera√ß√£o mais recente:', 
  '4¬™ intera√ß√£o mais recente:',
  '5¬™ intera√ß√£o mais recente:'
];

// Ordenar por created_at DESC (mais recente primeiro)
const sortedInteractions = interactions.sort((a, b) => 
  new Date(b.json.created_at) - new Date(a.json.created_at)
);

let formatted = '';

sortedInteractions.forEach((interaction, index) => {
  const data = interaction.json;

  formatted += `# ${formatLabels[index]}\n`;
  formatted += `Mensagem do agente: ${data.agent_message}\n`;
  formatted += `Timestamp: ${data.agent_timestamp_formatted}\n`;
  formatted += `Enviado por: ${data.agent_name}\n\n`;

  if (data.user_message && data.user_message.trim() !== '') {
    formatted += `Resposta do usu√°rio: ${data.user_message}\n`;
    formatted += `Timestamp: ${data.user_timestamp_formatted}\n\n`;
  } else {
    formatted += `Resposta do usu√°rio: [AGUARDANDO RESPOSTA]\n`;
    formatted += `Timestamp: [PENDENTE]\n\n`;
  }
});

return [{ json: { formatted_interactions: formatted.trim() } }];

// Atualizar intera√ß√£o pendente com resposta do usu√°rio
const userMessage = $('concatenacao').item.json.text_message;
const phoneCanon = $('setWebhookData').item.json.phone_canon;

// Gerar timestamp formatado para SP
const now = new Date();
const spTimeFormatted = now.toLocaleString('pt-BR', {
  timeZone: 'America/Sao_Paulo',
  day: '2-digit',
  month: '2-digit', 
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
}).replace(',', ' -');

return [{
  json: {
    phone_canon: phoneCanon,
    user_message: userMessage,
    user_timestamp_formatted: spTimeFormatted,
    user_timestamp: now.toISOString()
  }
}];

// Encontrar a intera√ß√£o mais recente sem resposta do usu√°rio
const interactions = $('getRecentInteractions').all();
const userResponseData = $('updateUserResponse').item.json;

// Filtrar intera√ß√µes sem resposta e encontrar a mais recente
const pendingInteractions = interactions.filter(item => 
  item.json.user_message === null || item.json.user_message === ''
);

if (pendingInteractions.length === 0) {
  // Nenhuma intera√ß√£o pendente - usu√°rio pode estar respondendo algo muito antigo
  return [{ json: { no_update_needed: true } }];
}

// Ordenar por created_at DESC e pegar a primeira (mais recente por timestamp)
const latestPending = pendingInteractions.sort((a, b) => 
  new Date(b.json.created_at) - new Date(a.json.created_at)
)[0];

return [{
  json: {
    ...userResponseData,
    interaction_id: latestPending.json.id,
    interaction_sequence: latestPending.json.interaction_sequence
  }
}];

// Preparar dados para nova intera√ß√£o do agente Marcos
const phoneCanon = $('setWebhookData').first().json.phone_canon;
const agentMessage = $('AI Agent - Marcos Atendimento').item.json.output;
const agentName = 'Marcos - Agente Atendimento';

// Gerar timestamp formatado para SP
const now = new Date();
const spTimeFormatted = now.toLocaleString('pt-BR', {
  timeZone: 'America/Sao_Paulo',
  day: '2-digit',
  month: '2-digit', 
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
}).replace(',', ' -');

return [{
  json: {
    phone_canon: phoneCanon,
    agent_message: agentMessage,
    agent_name: agentName,
    agent_timestamp_formatted: spTimeFormatted
  }
}];

// Nova l√≥gica: sempre sequence 1 para nova mensagem
const newInteractionData = $('prepareNewInteraction').first().json;

// Nova mensagem SEMPRE vai para sequence 1
const nextSequence = 1;

return [{
  json: {
    ...newInteractionData,
    interaction_sequence: nextSequence
  }
}];

// Usar dados j√° dispon√≠veis de getRecentInteractions
const allInteractions = $('getRecentInteractions').all();
const newInteractionData = $('calculateNextSequence').first().json;

if (allInteractions.length >= 5) {
  // Encontrar a intera√ß√£o mais antiga (menor created_at)
  const oldestInteraction = allInteractions.sort((a, b) => 
    new Date(a.json.created_at) - new Date(b.json.created_at)
  )[0];

  return [{
    json: {
      ...newInteractionData,
      should_delete: true,
      delete_id: oldestInteraction.json.id
    }
  }];
} else {
  return [{
    json: {
      ...newInteractionData,
      should_delete: false
    }
  }];
}

// Acessando o campo phone corretamente dentro do objeto body
const originalPhone = $input.item.json.body.phone;

// Verifica se originalPhone existe antes de continuar
if (!originalPhone) {
  console.log("N√∫mero de telefone n√£o encontrado no payload");
  // Mant√©m o objeto original se n√£o encontrar o telefone
  return $input.item;
}

// Vamos verificar se precisamos inserir o 9 e onde
let normalizedPhone = originalPhone;

// Verifica se o n√∫mero tem formato brasileiro (come√ßa com 55)
if (originalPhone.startsWith("55")) {
  // Extrai o DDD (2 d√≠gitos ap√≥s o 55)
  const ddd = originalPhone.substring(2, 4);

  // Extrai o resto do n√∫mero
  const restOfNumber = originalPhone.substring(4);

  // Se o restante do n√∫mero tiver 8 d√≠gitos, inserimos o 9
  if (restOfNumber.length === 8) {
    normalizedPhone = `55${ddd}9${restOfNumber}`;
    console.log(`N√∫mero normalizado: ${normalizedPhone}`);
  }
}

// Mant√©m o n√∫mero original nos dados e adiciona o normalizado
$input.item.json.originalPhone = originalPhone;
$input.item.json.normalizedPhone = normalizedPhone;

return $input.item;
